// Libs
import React, { Component } from 'react';
import mapboxgl from 'mapbox-gl';

class Menu extends Component {

  state = {
    buttonList: [
      "Mapeamento",
      "Estatísticas",
      "Grid"
    ],
    mappingList: [
      "Sócio-Econômico",
      "Demandas e Entregas",
      "ONG's"
    ],
    isMapping: false,
    isSelected: '',
  };

  handleIsMappingOpen = (button) => {
    this.setState({ 
      isSelected: button,
      isMapping: !this.state.isMapping,
    });
  }

  // handleSelected = () => {
  //   this.setState({
  //     isSelected: !this.state.isSelected,
  //   })
  // }

  handleSelected = (button) => {
    console.log('button', button)

    this.setState({ isSelected: button });
  }

  renderMapping = () => (
    <div className="container_mapping">
      <div className="container_mapping-item">
        <div className="mapping_item-checkbox" />
        <p className="mapping_item-text">Sócio-Econômico</p>
      </div>
      <div className="container_mapping-item">
        <div className="mapping_item-checkbox" />
        <p className="mapping_item-text">Demandas e Entregas</p>
      </div>
      <div className="container_mapping-item">
        <div className="mapping_item-checkbox" />
        <p className="mapping_item-text">ONG's</p>
      </div>
    </div>
  )

  render() {
    return (
      <aside className="container_menu">
        <span className="container_menu-circle"></span>
        {this.state.buttonList.map((button) => (
          <>
            <button
              key={button}
              className="container_menu-button"
              style={{ backgroundColor: this.state.isSelected === button ? '#F7BBA9' : null }}
              onClick={button === 'Mapeamento' ? () => this.handleIsMappingOpen(button) : () => this.handleSelected(button)}
            >
              <p className="menu_button-title">{button}</p>
            </button>
          </>
          )
		    )}
        {this.state.isMapping && this.renderMapping()}


        {/* <button
          className="container_menu-button"
          style={{background: this.state.isMapping ? '#F7BBA9' : null}}
          onClick={this.handleIsMappingOpen}
        >
          <p className="menu_button-title">Mapeamento</p>
        </button>
        {this.state.isMapping && this.renderMapping()}
        <button className="container_menu-button">
          <p className="menu_button-title">Estatísticas</p>
        </button>
        <button className="container_menu-button">
          <p className="menu_button-title">Strech</p>
        </button> */}
    </aside>
    );
  }
}

export default Menu;
