// Libs
import React, { Component } from 'react';

// Components
import Menu from '../components/Menu';

// Images
import dropdownIcon from '../assets/dropdown.svg';

class Grid extends Component {
  state = {
    tableTitles: [
      'Bairro',
      'Renda Média',
      'Nº de Casos',
      'Nº de Óbitos',
      'Demandas de Cestas',
      'Cestas Entregues',
      '%'
    ],
    tableDatas: [
      {
        neighborhood: 'Penha',
        incomeBracket: 'R$1.362,65',
        victims: 36,
        obitos: 21,
        basketDemand: 35,
        basketsDelivered: 26,
        percent: 30
      },
      {
        neighborhood: 'Campo Grande',
        incomeBracket: 'R$6.000,21',
        victims: 56,
        obitos: 21,
        basketDemand: 55,
        basketsDelivered: 26,
        percent: 40
      },
      {
        neighborhood: 'Bangu',
        incomeBracket: 'R$1.362,65',
        victims: 54,
        obitos: 21,
        basketDemand: 676,
        basketsDelivered: 332,
        percent: 50
      },
      {
        neighborhood: 'Santa Cruz',
        incomeBracket: 'R$1.362,65',
        victims: 3,
        obitos: 21,
        basketDemand: 345,
        basketsDelivered: 222,
        percent: 60
      },
      {
        neighborhood: 'Ipanema',
        incomeBracket: 'R$1.362,65',
        victims: 543,
        obitos: 21,
        basketDemand: 765,
        basketsDelivered: 300,
        percent: 70
      },
      {
        neighborhood: 'Morro dos Prazeres',
        incomeBracket: 'R$1.362,65',
        victims: 54,
        obitos: 28,
        basketDemand: 888,
        basketsDelivered: 79,
        percent: 90
      },
      {
        neighborhood: 'Santa Teresa',
        incomeBracket: 'R$1.362,65',
        victims: 5,
        obitos: 21,
        basketDemand: 644,
        basketsDelivered: 644,
        percent: 100
      },

    ],
    selectedValue: 'Selecionar',
    selectedItems: [
      'Selecionar',
      'Bairros A-Z',
      'Bairros Z-A',
      'Maior Renda Média',
      'Menor Renda Média',
      'Maior Nº de Casos',
      'Menor Nº de Casos',
      'Maior Ocorrência de Óbitos',
      'Menor Ocorrência de Óbitos',
      'Maior Demanda de Cestas',
      'Menor Demanda de Cestas',
      'Maior Nº de Cestas Entregues',
      'Menor Nº de Cestas Entregues',
    ],
    isDropdownSelected: false,
  }

  handleSelectedValue = (item) => {
    this.setState({
      selectedValue: item,
      isDropdownSelected: false,
    });

    if (item === "Bairros A-Z") {
      const teste = this.state.tableDatas.sort((item, b) => item.neighborhood.localeCompare(b.neighborhood))

      this.setState({ tableDatas: teste })
    }

    if (item === "Bairros Z-A") {
      const teste = this.state.tableDatas.reverse((item, b) => item.neighborhood.localeCompare(b.neighborhood))

      this.setState({ tableDatas: teste })
    }

    if (item === 'Maior Renda Média') {
      // const teste = this.state.tableDatas.reverse((item, b) => item.incomeBracket.localeCompare(b.incomeBracket))

      const teste = this.state.tableDatas.sort(function(a, b){ 
        if(a.incomeBracket < b.incomeBracket) { 
          return 1;
        } 
        if(a.incomeBracket > b.incomeBracket) { 
          return -1; 
        } 
        return 0; 
      })

      this.setState({ tableDatas: teste })
    }

    if (item === 'Menor Renda Média') {
      // const teste = this.state.tableDatas.sort((item, b) => item.incomeBracket.localeCompare(b.incomeBracket))

      const teste = this.state.tableDatas.sort(function(a, b){ 
        if(a.incomeBracket < b.incomeBracket) { 
          return -1;
        } 
        if(a.incomeBracket > b.incomeBracket) { 
          return 1; 
        } 
        return 0; 
      })

      this.setState({ tableDatas: teste })
    }

    if (item === 'Maior Nº de Casos') {
      const teste = this.state.tableDatas.sort((item, b) => item.victims.localeCompare(b.victims))

      // const teste = this.state.tableDatas.sort(function(a, b){ 
      //   if(a.incomeBracket < b.incomeBracket) { 
      //     return -1;
      //   } 
      //   if(a.incomeBracket > b.incomeBracket) { 
      //     return 1; 
      //   } 
      //   return 0; 
      // })

      this.setState({ tableDatas: teste })
    }

    if (item === 'Menor Nº de Casos') {
      const teste = this.state.tableDatas.reverse((item, b) => item.victims.localeCompare(b.victims))

      this.setState({ tableDatas: teste })
    }

    if (item === 'Maior Ocorrência de Óbitos') {
      const teste = this.state.tableDatas.sort((item, b) => item.obitos.localeCompare(b.obitos))

      this.setState({ tableDatas: teste })
    }

    if (item === 'Menor Ocorrência de Óbitos') {
      const teste = this.state.tableDatas.reverse((item, b) => item.obitos.localeCompare(b.obitos))

      this.setState({ tableDatas: teste })
    }

    if (item === 'Maior Demanda de Cestas') {
      const teste = this.state.tableDatas.sort((item, b) => item.basketDemand.localeCompare(b.basketDemand))

      this.setState({ tableDatas: teste })
    }

    if (item === 'Menor Demanda de Cestas') {
      const teste = this.state.tableDatas.sort((item, b) => item.basketDemand.localeCompare(b.basketDemand))

      this.setState({ tableDatas: teste })
    }

    if (item === 'Maior Nº de Cestas Entregues') {
      const teste = this.state.tableDatas.sort((item, b) => item.basketsDelivered.localeCompare(b.basketsDelivered))

      this.setState({ tableDatas: teste })
    }

    if (item === 'Menor Nº de Cestas Entregues') {
      const teste = this.state.tableDatas.reverse((item, b) => item.basketsDelivered.localeCompare(b.basketsDelivered))

      this.setState({ tableDatas: teste })
    }

    // if (item === '%') {
    //   const teste = this.state.tableDatas.sort((item, b) => item.percent.localeCompare(b.percent))

    //   this.setState({ tableDatas: teste })
    // }

  }

  handleSelectOpen = () => {
    this.setState({
      isDropdownSelected: !this.state.isDropdownSelected,
    });
  }

  handleSelectClose = () => {
    if (this.state.isDropdownSelected) {
      this.setState({
        isDropdownSelected: false,
      });
    }
  }

  handleTeste = (title) => {
    if (title === 'Bairro') {      
      const teste = this.state.tableDatas.sort((item, b) => item.neighborhood.localeCompare(b.neighborhood))

      this.setState({ tableDatas: teste })
    }

    if (title === 'Renda Média') {
      const teste = this.state.tableDatas.sort((item, b) => item.incomeBracket.localeCompare(b.incomeBracket))

      this.setState({ tableDatas: teste })
    }

    if (title === 'Nº de Casos') {
      const teste = this.state.tableDatas.sort((item, b) => item.victims.localeCompare(b.victims))

      this.setState({ tableDatas: teste })
    }

    if (title === 'Nº de Óbitos') {
      const teste = this.state.tableDatas.sort((item, b) => item.victims.localeCompare(b.victims))

      this.setState({ tableDatas: teste })
    }

    if (title === 'Nº de Óbitos') {
      const teste = this.state.tableDatas.sort((item, b) => item.obitos.localeCompare(b.obitos))

      this.setState({ tableDatas: teste })
    }

    if (title === 'Demandas de Cestas') {
      const teste = this.state.tableDatas.sort((item, b) => item.basketDemand.localeCompare(b.basketDemand))

      this.setState({ tableDatas: teste })
    }

    if (title === 'Cestas Entregues') {
      const teste = this.state.tableDatas.sort((item, b) => item.basketsDelivered.localeCompare(b.basketsDelivered))

      this.setState({ tableDatas: teste })
    }

    if (title === '%') {
      const teste = this.state.tableDatas.sort((item, b) => item.percent.localeCompare(b.percent))

      this.setState({ tableDatas: teste })
    }
  }

  render() {
    const { selectedValue, isDropdownSelected, selectedItems, tableTitles, tableDatas } = this.state;
    return (
      <div className="Grid">
        <Menu />
        <div className="container_grid">
          <div className="container_grid-content" onClick={this.handleSelectClose}>
            <div className="container_grid-content--header">
              <h2 className="header_title">Relação de Bairros</h2>
              <div className="container_header-sortBy">
                <p className="container_header-sortBy--title">Classificar por:</p>
                <div className="container_header-sortBy--input">
                  <div className="header_sortBy-input" onClick={this.handleSelectOpen}>
                    <p className="sortBy_textSeleted">{selectedValue}</p>
                    <img src={dropdownIcon} alt="dropdown" />
                  </div>
                  {isDropdownSelected && (
                    <div className="sortBy_selectedItem">
                      {selectedItems.map((item, index) => (
                        <p
                          className="sortBy_selectedItem-text"
                          style={{ borderRadius: index === selectedItems.length - 1 ? '0 0 15px 15px' : null }}
                          onClick={() => this.handleSelectedValue(item)}
                          key={index}
                        >
                          {item}
                        </p>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>
            <div className="table">
              <table className="container_table">
                <thead className="container_table-head">
                  <tr className="container_table-tr">
                    {tableTitles.map((title, index) => (
                      <th key={index} onClick={() => this.handleTeste(title)} className="container_table-title">{title}</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {tableDatas.map((item, index) => (
                    <tr key={index} className="container_table-tr">
                      <td className="container_table-text">{item.neighborhood}</td>
                      <td className="container_table-text">{item.incomeBracket}</td>
                      <td className="container_table-text">{item.victims}</td>
                      <td className="container_table-text">{item.obitos}</td>
                      <td className="container_table-text">{item.basketDemand}</td>
                      <td className="container_table-text">{item.basketsDelivered}</td>
                      <td className="container_table-text">{item.percent}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Grid;
