// Libs
import React, { Component } from 'react';
import { Link } from 'react-router-dom';

// Images
// import checkboxIcon from '../assets/checkbox.svg';
// import checkIcon from '../assets/check.svg';
import Logo from '../assets/logo.png';

class Menu extends Component {
  state = {
    buttonList: [
      {
        label: "Mapeamento",
        route: "/"
      },
      {
        label: "Estatísticas",
        route: "/statistics"
      },
      {
        label: "Grid",
        route: "/grid"
      },      
    ],
    mappingList: [
      "Sócio-Econômico",
      "Demandas e Entregas",
      "ONG's"
    ],
    isMapping: false,
    isSelectedButton: '',
    isSelectedCheck: '',
  };

  handleIsMappingOpen = (item) => {
    this.setState({ 
      isSelectedButton: item,
      isMapping: !this.state.isMapping,
    });
  }

  handleMappingCheck = (item) => {
    this.setState({
      isSelectedCheck: item,
      // isSelectedCheck: [...this.state.isSelectedCheck, item],
    })
  }

  handleSelected = (label) => {    
    this.setState({ isSelectedButton: label });

    console.log('isSelectedButton label', this.state.isSelectedButton)
  }


  renderMapping = () => (
    <div className="container_mapping">
      {this.state.mappingList.map((item) => (
        <div key={item} className="container_mapping-item">
          <div
            className="mapping_item-checkbox"
            onClick={() => this.handleMappingCheck(item)}
          >
          </div>
          {/* {this.state.isSelectedCheck === item
            ? <img
                src={checkIcon}
                alt="check"
                className="mapping_item-checkbox"
              />
            : <img
                src={checkboxIcon}
                alt="checkbox"
                className="mapping_item-checkbox"
                onClick={() => this.handleMappingCheck(item)}
              />
          } */}
          <p className="mapping_item-text">{item}</p>
        </div>
      ))}
    </div>
  )


  render() {
    return (
      <aside className="container_menu" style={this.props.styles}>
        <figure className="container_menu-logo">
          <img src={Logo} alt="logo" />
        </figure>
        {this.state.buttonList.map(({label, route}) => (
          <>
            <Link
              key={label}
              className="container_menu-button"
              to={route}
              style={{ backgroundColor: this.state.isSelectedButton && this.state.isSelectedButton === label ? '#FBB03B' : null}}
              onClick={label === 'Mapeamento'
                ? () => this.handleIsMappingOpen(label)
                : () => this.handleSelected(label)
              }
            >
              <p 
                className="menu_button-title"
                style={{ color: this.state.isSelectedButton === label ? '#000' : null }}
              >
                {label}
              </p>
            </Link>
            {label === "Mapeamento" ? this.state.isMapping && this.renderMapping() : null}
          </>
        ))}
    </aside>
    );
  }
}

export default Menu;
