// Libs
import React, { Component } from 'react';

// Components
import Menu from '../components/Menu';

// Images
// import dropdownIcon from '../assets/dropdown.svg';
import dropdownBlack from '../assets/dropdownBlack.svg';
import Loading from '../assets/loading.svg';

// Api
import { getAllNeighborhood } from '../api';


class Grid extends Component {
  state = {
    tableTitles: [
      'Bairro',
      'Renda Média',
      'Nº de Casos',
      'Nº de Óbitos',
      'Demandas de Cestas',
      'Cestas Entregues',
      '%'
    ],
    selectedValue: 'Selecionar',
    selectedItems: [
      'Selecionar',
      'Bairros A-Z',
      'Bairros Z-A',
      'Maior Renda Média',
      'Menor Renda Média',
      'Maior Nº de Casos',
      'Menor Nº de Casos',
      'Maior Ocorrência de Óbitos',
      'Menor Ocorrência de Óbitos',
      'Maior Demanda de Cestas',
      'Menor Demanda de Cestas',
      'Maior Nº de Cestas Entregues',
      'Menor Nº de Cestas Entregues',
    ],
    isDropdownSelected: false,
    neighborhoodData: [],
    isFetching: false,
  }

  componentDidMount() {
    this.fetchNeighborhood();
  }

  async fetchNeighborhood() {

    try {

      this.setState({ isFetching: true });

      const response = await getAllNeighborhood();
      console.log(response)

      let uc = response.data.data.map((item) => ({
        ...item,
        _souce: {
          ...item._source,
          idosos: parseInt(item._source.idosos, 10)
        }
      }))

      console.log('uc', uc)

      this.setState({
        neighborhoodData: response.data.data,
        isFetching: false,
      });

    } catch (error) {
      console.log(error);
      console.log(error.response);
      this.setState({ isFetching: false });
    }
  }

  handleSelectedValue = (item) => {
    this.setState({
      selectedValue: item,
      isDropdownSelected: false,
    });

    if (item === "Bairros A-Z") {
      const teste = this.state.neighborhoodData.sort((item, b) => item._source.Bairro.localeCompare(b._source.Bairro))

      this.setState({ neighborhoodData: teste })
    }

    if (item === "Bairros Z-A") {
      const teste = this.state.neighborhoodData.reverse((item, b) => item._source.Bairro.localeCompare(b._source.Bairro))

      this.setState({ neighborhoodData: teste })
    }

    if (item === 'Maior Renda Média') {

      const teste = this.state.neighborhoodData.sort((a, b) => Number(a.Renda_per_capita_sal_min) - Number(b.Renda_per_capita_sal_min));

      this.setState({ neighborhoodData: teste })
      console.log('teste', teste)

    }

    if (item === 'Menor Renda Média') {
      // const teste = this.state.neighborhoodData.sort((item, b) => item.incomeBracket.localeCompare(b.incomeBracket))

      const teste = this.state.neighborhoodData.sort(function (a, b) {
        if (a.Renda_per_capita_sal_min > b.Renda_per_capita_sal_min) {
          return -1;
        }
        return 0
      })

      this.setState({ neighborhoodData: teste })
    }

    if (item === 'Maior Nº de Casos') {
      // const teste = this.state.neighborhoodData.sort((item, b) => item.victims.localeCompare(b.victims))

      const teste = this.state.neighborhoodData.sort(function (a, b) {
        if (a.CasosConfirmados > b.CasosConfirmados) {
          return 1;
        }
        return 0
      })

      this.setState({ neighborhoodData: teste })
    }

    if (item === 'Menor Nº de Casos') {
      const teste = this.state.neighborhoodData.reverse((item, b) => item._source.CasosConfirmados.localeCompare(b.CasosConfirmados))

      this.setState({ neighborhoodData: teste })
    }

    if (item === 'Maior Ocorrência de Óbitos') {

      const teste = this.state.neighborhoodData.sort(function (a, b) {
        if (a.Obitos > b.Obitos) {
          return 1;
        }
        return 0
      })

      this.setState({ neighborhoodData: teste })
    }

    if (item === 'Menor Ocorrência de Óbitos') {
      const teste = this.state.neighborhoodData.reverse((item, b) => item._source.Obitos.localeCompare(b.Obitos))

      this.setState({ neighborhoodData: teste })
    }

    // if (item === 'Maior Demanda de Cestas') {

    //   const teste = this.state.neighborhoodData.sort(function(a, b){ 
    //     if(a.incomeBracket < b.incomeBracket) { 
    //       return -1;
    //     } 
    //     if(a.incomeBracket > b.incomeBracket) { 
    //       return 1; 
    //     } 
    //     return 0; 
    //   })

    //   this.setState({ neighborhoodData: teste })
    // }

    // if (item === 'Menor Demanda de Cestas') {
    //   const teste = this.state.neighborhoodData.reverse((item, b) => item.basketDemand.localeCompare(b.basketDemand))

    //   this.setState({ neighborhoodData: teste })

    // }

    // if (item === 'Maior Nº de Cestas Entregues') {

    //   const teste = this.state.neighborhoodData.sort(function(a, b){ 
    //     if(a.incomeBracket < b.incomeBracket) { 
    //       return -1;
    //     } 
    //     if(a.incomeBracket > b.incomeBracket) { 
    //       return 1; 
    //     } 
    //     return 0; 
    //   })

    //   this.setState({ neighborhoodData: teste })

    // }

    // if (item === 'Menor Nº de Cestas Entregues') {
    //   const teste = this.state.neighborhoodData.reverse((item, b) => item.basketsDelivered.localeCompare(b.basketsDelivered))

    //   this.setState({ neighborhoodData: teste })

    // }
  }

  handleSelectOpen = () => {
    this.setState({
      isDropdownSelected: !this.state.isDropdownSelected,
    });
  }

  handleSelectClose = () => {
    if (this.state.isDropdownSelected) {
      this.setState({
        isDropdownSelected: false,
      });
    }
  }

  render() {
    console.log('neighborhoodData', this.state.neighborhoodData)

    const { selectedValue, isDropdownSelected, selectedItems, tableTitles, neighborhoodData } = this.state;
    return (
      <div className="Grid">
        <Menu />
        <div className="container_grid">
          <div className='container_map-rio'>
            <p className='map_rio-text'>
              riocontra
            </p>
            <p className='map_rio-text' style={{ color: '#F05123' }}>corona</p>
          </div>
          <div className="container_grid-content" onClick={this.handleSelectClose}>
            <div className="container_grid-content--header">
              <h2 className="header_title">Estatísticas por Bairro</h2>
              <div className="container_header-sortBy">
                <p className="container_header-sortBy--title">Classificar por:</p>
                <div className="container_header-sortBy--input">
                  <div className="header_sortBy-input" onClick={this.handleSelectOpen}>
                    <p className="sortBy_textSeleted">{selectedValue}</p>
                    <img src={dropdownBlack} alt="dropdown" />
                  </div>
                  {isDropdownSelected && (
                    <div className="sortBy_selectedItem">
                      {selectedItems.map((item, index) => (
                        <p
                          className="sortBy_selectedItem-text"
                          onClick={() => this.handleSelectedValue(item)}
                          key={index}
                        >
                          {item}
                        </p>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>
            <div className="table">
              <table className="container_table">
                <thead className="container_table-head">
                  <tr className="container_table-tr">
                    {tableTitles.map((title, index) => (
                      <th
                        key={index}
                        className="container_table-title"
                        style={{ textAlign: title === 'Bairro' ? 'left' : 'center' }}
                      >
                        {title}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {neighborhoodData.map((item, index) => (
                    <tr key={index} className="container_table-tr">
                      <td className="container_table-text container_table-text--neighborhood">{item._source.Bairro}</td>
                      <td className="container_table-text">R$ {item._source.Renda_per_capita_sal_min.replace(",", ".")*1000},00</td>
                      <td className="container_table-text">{item._source.CasosConfirmados}</td>
                      <td className="container_table-text">{item._source.Obitos}</td>
                      {item._source["Quantidade de cestas doadas"] && item._source["Quantidade de cestas demandadas"] !== undefined ? (
                        <td className="container_table-text">{item._source["Quantidade de cestas demandadas"].toFixed(0)}</td>
                      ) : (
                        <td className="container_table-text">0</td>
                      )}
                      {item._source["Quantidade de cestas demandadas"] && item._source["Quantidade de cestas doadas"] !== undefined ? (
                        <td className="container_table-text">{item._source["Quantidade de cestas doadas"].toFixed(0)}</td>
                      ) : (
                        <td className="container_table-text">0</td>
                      )}
                      {item && item._source["Quantidade de cestas demandadas"] && item._source["Quantidade de cestas doadas"] ? (
                        <td className="container_table-text">
                          {(item._source["Quantidade de cestas doadas"]*100 / item._source["Quantidade de cestas demandadas"]).toFixed(0)}%
                        </td>
                      ) : (
                        <td className="container_table-text">0</td>
                      )}
                    </tr>
                  ))}
                </tbody>
              </table>
              {this.state.isFetching && (
                <figure className="container_table-loader">
                  <img src={Loading} alt="Carregando..." className="table-loader" />
                </figure>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Grid;
