// Libs
import React, { Component } from 'react';

// Components
import Menu from '../components/Menu';

// Images
import dropdownIcon from '../assets/dropdown.svg';

class Grid extends Component {
  state = {
    tableTitles: [
      'Bairro',
      'Faixa de Renda',
      'Nº de Vítimas',
      'Demandas de Cestas',
      'Cestas Entregues',
      '%'
    ],
    selectedValue: 'Selecionar',
    selectedItems: [
      'teste',
      'testando',
      'loren',
      'ipsun'
    ]
  }

  handleSelectedValue = (item) => {
		this.setState({
			selectedValue: item,
		});
	}

  render() {
    return (
      <div className="Grid">
        <Menu />
        <div className="container_grid">
          <div className="container_grid-content">
            <div className="container_grid-content--header">
              <h2>Relação de Bairros</h2>
              <div className="container_header-sortBy">
                <p>Classificar por:</p>
                <div className="container_header-sortBy--input">
                  <p className="sortBy_textSeleted">{this.state.selectedValue}</p>
                  <img src={dropdownIcon} alt="dropdown" />
                </div>
                { this.state.isSelected && (
                  <div>
                  {this.state.selectedItems.map((item, index) => (
                    <p
                      onClick={() => this.handleSelectedValue(item)}
                      key={index}
                    >
                      {item}
                    </p>
                  ))}
						    </div>
                )}
              </div>
            </div>
            <table className="container_table">
              <thead className="container_table-head">
                <tr className="container_table-tr">
                  {this.state.tableTitles.map((title) => (
                    <th className="container_table-title">{title}</th>
                  ))}
                </tr>
              </thead>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default Grid;
