// Libs
import React, { Component } from 'react';
import { Link, Redirect } from 'react-router-dom';

// Images
import checkboxIcon from '../assets/checkbox.svg';
import checkIcon from '../assets/check.svg';

class Menu extends Component {

  state = {
    // buttonList: [
    //   {
    //     label: "Mapeamento",
    //     route: "/"
    //   },
    //   {
    //     label: "Estatísticas",
    //     route: "/statistics"
    //   },
    //   {
    //     label: "Grid",
    //     route: "/grid"
    //   },      
    // ],
    mappingList: [
      "Sócio-Econômico",
      "Demandas e Entregas",
      "ONG's"
    ],
    isMapping: false,
    isSelectedButton: '',
    isSelectedCheck: '',
    redirect: 'statistic',
    // isSelectedCheck: [],
  };

  handleIsMappingOpen = (item) => {
    this.setState({ 
      isSelectedButton: item,
      isMapping: !this.state.isMapping,
    });
  }

  handleMappingCheck = (item) => {
    this.setState({
      isSelectedCheck: item,
      // isSelectedCheck: [...this.state.isSelectedCheck, item],
    })
  }

  // handleSelected = (label) => {    
  //   this.setState({ isSelectedButton: label });
  // }

  handleSelected = (item) => {    
    this.setState({ redirect: item });
  }

  renderMapping = () => (
    <div className="container_mapping">
      {this.state.mappingList.map((item) => (
        <div key={item} className="container_mapping-item">
          {/* <div
            className="mapping_item-checkbox"
            onClick={() => this.handleMappingCheck(item)}
          >
          </div> */}
          {this.state.isSelectedCheck === item
            ? <img
                src={checkIcon}
                alt="check"
                className="mapping_item-checkbox"
              />
            : <img
                src={checkboxIcon}
                alt="checkbox"
                className="mapping_item-checkbox"
                onClick={() => this.handleMappingCheck(item)}
              />
          }
          <p className="mapping_item-text">{item}</p>
        </div>
      ))}
    </div>
  )

  renderButton = item => (
    <Link
      to={`${item} === "Mapeamento" ? "/" : /${item}`}
      className="container_menu-button"
      onClick={item === "Mapeamento" ? () => this.handleIsMappingOpen(item) : () => this.handleSelected(item)}
      style={{ background: this.state.redirect === item ? '#F7BBA9' : null }}
    >
      <p className="menu_button-title">
        {item === "Grid" ? item : "Estatísticas"}
      </p>
    </Link>
  )

  render() {
    return (
      <aside className="container_menu" style={this.props.styles}>
        <span className="container_menu-circle" />
        {/* <Link
          className="container_menu-button"
          style={{background: this.state.isMapping ? '#F7BBA9' : null}}
          onClick={this.handleIsMappingOpen}
          to={'/'}
        >
          <p className="menu_button-title">Mapeamento</p>
        </Link> */}
        {this.renderButton("Mapeamento")}
        {this.state.isMapping && this.renderMapping()}
        {this.renderButton("Estatísticas")}
        {this.renderButton("Grid")}

        {/* {this.state.buttonList.map(({label, route}) => (
          <button
            key={label}
            className="container_menu-button"
            style={{ backgroundColor: this.state.isSelectedButton === label ? '#F7BBA9' : null }}
            to={route}
            onClick={label === 'Mapeamento'
              ? () => this.handleIsMappingOpen(label)
              : () => this.handleSelected(label)
            }
          >
            <p className="menu_button-title">{label}</p>
          </button>
        ))} */}
          {/* {this.state.isMapping && this.renderMapping()} */}




        {/* <Link
          className="container_menu-button"
          style={{background: this.state.isMapping ? '#F7BBA9' : null}}
          onClick={this.handleIsMappingOpen}
          to={'/'}
        >
          <p className="menu_button-title">Mapeamento</p>
        </Link>
        {this.state.isMapping && this.renderMapping()}
        <Link
          className="container_menu-button"
          style={{ backgroundColor: this.state.isSelectedButton === label ? '#F7BBA9' : null }}
          onClick={this.handleSelected}
          to={'/statistics'}
        >
          <p className="menu_button-title">Estatísticas</p>
        </Link>
        <Link
          className="container_menu-button"
          style={{ backgroundColor: this.state.isSelectedButton === label ? '#F7BBA9' : null }}
          onClick={this.handleSelected}
          to={'/grid'}
        >
          <p className="menu_button-title">Strech</p>
        </Link> */}
    </aside>
    );
  }
}

export default Menu;
